/**
 * Appearance settings for the negative values in the Bubble series.
 */
export interface NegativeBubbleValues {
    /**
     * The color of the Chart negative bubble values.
     */
    color?: string;
    /**
     * If set to `true`, the Chart displays the negative bubbles.
     * By default, the negative bubbles are not displayed.
     */
    visible?: boolean;
}
